var contents = new Array("\n\r\n\rبازگشت به پورتال جامع برنامه‏ها\n\r\n\rدر هر صفحه، امکان بازگشت به پورتال جامع برنامه‏ها وجود دارد. ابزار بازگشت به پورتال، به شکل خانه در شکل 5 نمایش داده شده است. دسترسی به پورتال از طریق این ابزار در تمامی صفحات امکان‏پذیر است. &nbsp;شکل 5\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;5  ابزار دسترسی به پورتال در هر صفحه      \n\r\n\rامکان انتخاب رنگ دلخواه صفحات\n\r\n\rدر هر صفحه امکان انتخاب رنگ دلخواه با کلیک بر ابزار نمایش داده شده در ‏شكل 6 و ‏شكل 7 امکان‏پذیر است. \n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;6 ابزار  تغییر رنگ صفحات  \n\r\n\r  &nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;   \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;7  امکان تغییر رنگ&nbsp;و تم&nbsp;   صفحات  \n\r\n\r  &nbsp;\n\r\n\r&nbsp;بازگشت به صفحه قبل   \n\r\n\rدر هر صفحه برنامه، ابزار اشاره شده در ‏شكل 8 جهت بازگشت به صفحه قبلی بکار می‏رود.\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\rشکل&nbsp;8 ابزار بازگشت به صفحه قبل    \n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rخروج کاربر از برنامه\n\r\n\r&nbsp;\n\r\n\rامکان خروج کاربر از برنامه، از طریق دکمه نمایش داده شده در ‏شكل 9 فراهم شده است.\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;9 خروج کاربر\n\r\n\r&nbsp;\n\r\n\rتوجه: در این برنامه، تمامی دکمه‏های عملیاتی در بالای صفحات و فرم‏ها قرار دارند.\n\r\n\r&nbsp;\n\r\n\r&nbsp;نمایش تمام صفحه برنامه\n\r\n\r&nbsp;\n\r\n\rدر صورت نیاز به نمایش تمام صفحه برنامه، می‏توان از ابزار نمایش داده شده در شکل10 استفاده نمود.\n\r\n\r            \n\r\n\rشکل 10 نمایش تمام صفحه برنامه   \n\r\n\r&nbsp;\n\r\n\rنمایش تعداد درخواست ها و بازسپاری های جدید\n\r\n\r&nbsp;\n\r\n\rتعداد درخواست ها و بازسپاری های جدید دریافت شده توسط کاربر در هر لحظه در بالای صفحه نمایش داده می شود. شکل 11\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل 11 نمایش تعداد درخواست ها و بازسپاری های جدید\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r","دکمه های عملیاتی","topics\\ابزار_ها.htm","\n\r\n\rتعریف بازسپاری جدید\n\r\n\r  امکان تعریف بازسپاری جدید برای یک درخواست با استفاده از دکمه ثبت بازسپاری فراهم شده است. با انتخاب یک درخواست و کلیک بر این دکمه، فرم ثبت بازسپاری نمایش داده می شود که با تکمیل اطلاعات کلی بازسپاری، شرح بازسپاری، دریافت کنندگان، پیوست و یادآوری ، یک بازسپاری جدید به فهرست بازسپاری های موجود درخواست افزوده می شود. شکل61\n\r\n\r\n\r\n\rشکل 61&nbsp;  ثبت بازسپاری\n\r\n\rوضعیت بازسپاری \n\r\n\rامکان تعیین وضعیت بازسپاری از طریق فیلد وضعیت بازسپاری فراهم شده است. با انتخاب یکی از مقادیر فیلد وضعیت بازسپاری، می توان وضعیت بازسپاری را مشخص کرد. شکل 62\n\r\n\r                            \n\r\n\rتوجه: مقادیر فیلد وضعیت بازسپاری قابل مشاهده توسط مدیر مرکز عبارت از داخلی، ارسال به پیوند، عدم تایید مرکز و ارسال اطلاعات تکمیلی می باشد. \n\r\n\rتوجه: مقادیر فیلد وضعیت بازسپاری قابل مشاهده توسط پیوند عبارت از انجام شد، رد درخواست، نیاز به اطلاعات تکمیلی، تحت بررسی تکمیلی، تحت بررسی واحد تولید، تحت بررسی واحد تست، تحت بررسی واحد فروش، تحت بررسی واحد مالی، تحت بررسی مدیریت می باشد. \n\r\n\r\n\r\n\rشکل 62&nbsp; فیلد وضعیت های بازسپاری     \n\r\n\rفهرست بازسپاری      \n\r\n\rدر قسمت فهرست بازسپاری ها، تمامی بازسپاری های قبلی مربوط به یک درخواست، برای شخص دریافت کننده بازسپاری نمایش داده می شود. شکل 63\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 63&nbsp;فهرست بازسپاری ها\n\r\n\r\n\r\n\r     \n\r\n\r&nbsp;\n\r\n\r&nbsp;","بازسپاری","topics\\بازسپاری.htm","\n\r\n\rپوشه کار \n\r\n\rدر قسمت پوشه کار امکان نمایش فهرست درخواست ها و بازسپاری ها به ترتیب از آخرین تاریخ ثبت به اولین تاریخ ثبت، جستجوی درخواست ها براساس وضعیت، سیستم، زیرسیستم، گروه درخواست، اولویت، شماره درخواست، عنوان، تاریخ ثبت و شرح &nbsp;وجود دارد. شکل 19\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل 19 پوشه کار\n\r\n\r1.&nbsp; نمایش فهرست درخواست ها\n\r\n\r\n\r\n\rاطلاعات درخواست ها نظیر شماره درخواست، وضعیت، گروه درخواست، اولویت، مرکز، عنوان، زیرسیستم، تاریخ و ساعت ثبت، ثبت کننده و وجود فایل پیوست در این قسمت قابل مشاهده می باشد. با انتخاب هر ردیف در جدول فهرست درخواست ها، شرح و پیوست های مربوط به ان درخواست در سمت چپ جدول و &nbsp;بازسپاری های مربوط به آن درخواست در پایین جدول نمایش داده می شود. &nbsp;شکل 20\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; شكل&nbsp;20 نمایش فهرست درخواست ها\n\r\n\r&nbsp;\n\r\n\rتوجه: با راست کلیک روی درخواست و انتخاب گزینه نمایش نیز می توان به اطلاعات درخواست دست یافت. شکل 21\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;21 گزینه نمایش اطلاعات درخواست\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\rتوجه: کاربر می تواند با نشانگرهای بالا و پایین بین درخواستها حرکت و با کلید Enter ، درخواست خاصی را انتخاب نماید.\n\r\n\r      &nbsp;\n\r\n\r      \n\r\n\rتوجه: هنگامی که درخواستی ارسالی می باشد با آیکن نمایش داده شده در تصویر نمایش داده می شود. شکل 22\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل22 آیکن درخواست ارسالی\n\r\n\r&nbsp;\n\r\n\rتوجه:در صورتی که درخواستی ارسالی باشد و هنوز توسط دریافت کننده خوانده نشده باشد، در کنار آیکن مربوط به درخواست ارسالی، آیکن نمایش داده شده در تصویر زیر نیز نمایش داده می شود. شکل 23\n\r\n\r&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\r\n\r&nbsp;&nbsp;&nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;23 آیکن درخواست ارسالی خوانده نشده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\rتوجه: در صورتی که درخواستی ارسالی باشد و توسط دریافت کننده خوانده شده باشد، در کنار آیکن مربوط به درخواست ارسالی، آیکن نمایش داده شده در تصویر زیر نیز نمایش داده می شود. (در صورت خوانده شدن درخواست توسط دریافت کننده، دیگر اجازه ویرایش و حذف آن توسط ارسال کننده وجود ندارد) شکل24\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;24 آیکن درخواست ارسالی خوانده شده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\rتوجه: هنگامی که درخواستی دریافتی می باشد با آیکن نمایش داده شده در تصویر نمایش داده می شود. شکل 25      \n\r\n\r    \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;شكل&nbsp;25     آیکن درخواست دریافتی&nbsp;&nbsp; &nbsp;\n\r\n\r&nbsp;\n\r\n\rتوجه: در صورتی که درخواستی دریافتی باشد و هنوز توسط دریافت کننده خوانده نشده باشد، در کنار آیکن مربوط به درخواست دریافتی، آیکن نمایش داده شده در تصویر زیر &nbsp;و آیکن هشدار نیز نمایش داده می شود. &nbsp;شکل 26\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل26 آیکن درخواست دریافتی خوانده نشده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rتوجه: در صورتی که درخواستی دریافتی باشد و توسط دریافت کننده خوانده شده باشد، در کنار آیکن مربوط به درخواست دریافتی، آیکن نمایش داده شده در تصویر زیر نیز نمایش داده می شود. شکل 27\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشكل27 آیکن درخواست دریافتی خوانده شده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rتوجه: هنگامی که بازسپاری جدیدی دریافت می شود نیز علامت هشدار کنار درخواست مربوط به آن بازسپاری نمایش داده می شود، با این تفاوت که رنگ آیکن هشدار مربوط به بازسپاری جدید (آبی رنگ) متفاوت از آیکن هشدار مربوط به درخواست جدید (سبز رنگ) می باشد. شکل 28\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 28 آیکن بازسپاری جدید\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rتوجه: در فیلد پیوست،&nbsp; درخواست های پیوست دار با علامت کلیپس مشخص می شود، و پیوست های مربوط به آن در سمت چپ صفحه و زیر شرح نمایش داده می شوند. شکل 29\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 29 درخواست های پیوست دار\n\r\n\r&nbsp;\n\r\n\rتوجه: در فیلد یادداشت، درخواست های یادداشت دار با آیکن نمایش داده شده در تصویر مشخص می شوند و با قرار دادن مکان نما روی آیکن یادداشت، محتوای یادداشت دریافت شده نمایش داده می شود. شکل 30\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 30 نمایش یادداشت\n\r\n\rتوجه: چنانچه بخواهید فقط فهرست درخواست ها و بازسپاری های جدید دریافت شده نمایش داده شود، کافی است گزینه \"درخواست ها و بازسپاری های جدید\" را انتخاب نمایید. شکل 31\n\r\n\r\n\r\n\rشکل 31 نمایش&nbsp; فهرست درخواست ها و باز سپاری های جدید\n\r\n\r\n\r\n\rتوجه: با انتخاب گزینه \"درخواست های من\"، تمامی درخواست های ارجاع داده شده به کاربر نمایش داده می شود\n\r\n\rتوجه: با کلیک بر آیکن نمایش داده شده در تصویر می توان از کل صفحه جهت نمایش فهرست درخواست ها استفاده نمود. شکل 32\n\r\n\r\n\r\n\rشکل 32 آیکن نمایش تمام &nbsp;صفحه درخواست ها\n\r\n\rتوجه: &nbsp;پس از نمایش تمام صفحه درخواست ها، با کلیک مجدد روی این دکمه یا به کمک کلید Esc مجددا صفحه به اندازه اولیه خود بر می گردد.\n\r\n\r&nbsp;\n\r\n\r2. جستجوی درخواست ها\n\r\n\r&nbsp;\n\r\n\rجهت جستجوی اطلاعات درخواست ها می‏توان از فیلدهای استان، گروه مراکز، مرکز، وضعیت، سیستم، زیرسیستم، گروه درخواست، اولویت، شماره درخواست، عنوان، تاریخ ثبت و شرح در قسمت بالای صفحه استفاده نمود و در اسرع وقت به اطلاعات مورد نیاز دست یافت. شکل 33\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 33 جستجوی اطلاعات درخواست ها\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rتوجه: فیلدهای&nbsp; \"استان\" و \"گروه مراکز\" در قسمت جستجو فقط برای پیوند قابل مشاهده می باشد\n\r\n\r\n\r\n\rتوجه: فیلد \"مرکز\" در قسمت جستجو فقط برای پیوند و راهبر مراکز قابل مشاهده می باشد. (در صورت تک مرکز بودن، فهرست مراکز نمایش داده نمی شود و به صورت پیشفرض همان تک مرکز نمایش داده می شود)\n\r\n\r\n\r\n\rتوجه: امکان انتخاب چندگانه برای فیلدهای \"مرکز\"، \"زیر سیستم\" و \"گروه درخواست\"&nbsp; در قسمت جستجو وجود دارد.\n\r\n\r\n\r\n\rتوجه: امکان جستجوی مرکز به تنهایی وجود دارد ولی امکان جستجوی استان و گروه مرکز به تنهایی وجود ندارد و لازم است مرکز نیز انتخاب شود.\n\r\n\r&nbsp;\n\r\n\r3. تعریف درخواست جدید\n\r\n\r&nbsp;\n\r\n\rامکان تعریف درخواست جدید با استفاده از دکمه ثبت درخواست فراهم شده است. با کلیک بر این دکمه، فرم ثبت درخواست نمایش داده می شود که با تکمیل اطلاعات کلی درخواست، شرح درخواست، دریافت کنندگان، پیوست و یادآوری ، یک درخواست جدید به فهرست درخواست های موجود افزوده می شود. شکل 34\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 34 دکمه افزودن درخواست\n\r\n\r\n\r\n\r4. ویرایش اطلاعات درخواست\n\r\n\r&nbsp;\n\r\n\rبرای ویرایش اطلاعات درخواست نظیر اطلاعات کلی درخواست، شرح درخواست، دریافت کنندگان، پیوست و یادآوری می توان با کلیک بر دکمه ویرایش به فرم ویرایش درخواست دست یافت و تغییرات مورد نظر را اعمال کرد شکل&nbsp;35 شکل 36\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 35 دکمه ویرایش \n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 36 ویرایش اطلاعات درخواست \n\r\n\r\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rتوجه: ویرایش درخواست تا زمانی امکان پذیر است که درخواست توسط دریافت کننده آن مشاهده نشده باشد\n\r\n\r&nbsp;\n\r\n\rتوجه: با راست کلیک کردن روی هر درخواست در فرم پوشه کار نیز می توان به فرم ویرایش درخواست دست یافت. شکل 37\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 37 دسترسی به گزینه ویرایش با راست کلیک\n\r\n\r&nbsp;\n\r\n\r5. حذف در خواست\n\r\n\r&nbsp;\n\r\n\rامکان دسترسی به گزینه حذف درخواست با راست کلیک کردن روی درخواست مربوطه در صفحه پوشه کار وجود دارد. شکل 38\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 38 دسترسی به گزینه&nbsp; حذف با راست کلیک\n\r\n\r&nbsp;\n\r\n\rتوجه: حذف هر درخواست فقط زمانی ممکن است که توسط دریافت کننده آن مشاهده نشده باشد. \n\r\n\r&nbsp;\n\r\n\r\n\r\n\r6. بستن درخواست \n\r\n\r&nbsp;\n\r\n\r\n\r\n\rامکان دسترسی به گزینه بستن درخواست با راست کلیک کردن روی درخواست مربوطه در صفحه پوشه کار وجود دارد. شکل 39\n\r\n\r\n\r\n\rتوجه: فقط باز کننده درخواست و مدیر مرکز می توانند درخواست را ببندند. \n\r\n\r&nbsp;\n\r\n\r\n\r\n\r \n\r\n\r\n\r\n\rشکل 39 بستن درخواست \n\r\n\r\n\r\n\r7. فهرست نمایش بازسپاری&nbsp; \n\r\n\rاطلاعات بازسپاری مربوط به هر درخواست انتخاب شده نظیر شماره بازسپاری، اولویت، تاریخ ثبت، عنوان، وضعیت، نوع کار انجام شده، ثبت کننده و وجود فایل پیوست در این قسمت قابل مشاهده می باشد. با انتخاب هر ردیف در جدول فهرست بازسپاری ها، شرح و پیوست های مربوط به ان بازسپاری در سمت چپ جدول نمایش داده می شود. شکل 40\n\r\n\r\n\r\n\rشکل 40 نمایش فهرست بازسپاری\n\r\n\rتوجه: با راست کلیک روی هر بازسپاری و انتخاب گزینه نمایش نیز می توان به اطلاعات هر بازسپاری دست یافت. شکل 41\n\r\n\r \n\r\n\rشکل 41 گزینه نمایش در راست کلیک \n\r\n\rتوجه: هنگامی که بازسپاری جدیدی دریافت می شود، علامت هشدار (آبی رنگ) در کنار درخواست مربوط به آن بازسپاری &nbsp;نمایش داده می شود. شکل 42\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 42 آیکن هشدار بازسپاری جدید\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\rتوجه: &nbsp;هنگامی که بازسپاری ارسال می شود، با ایکن نمایش داده شده در تصویر زیر نمایش داده می شود. شکل 43\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rشکل 43 آیکن ارسال بازسپاری\n\r\n\r&nbsp;\n\r\n\rتوجه: &nbsp;هنگامی که بازسپاری ارسالی توسط دریافت کننده خوانده نشده باشد، با ایکن نمایش داده شده در تصویر زیر نمایش داده می شود. شکل 44 \n\r\n\r&nbsp;\n\r\n\r\n\r\n\rشکل 44 آیکن ارسال بازسپاری و خوانده نشده توسط دریافت کننده \n\r\n\r&nbsp;\n\r\n\rتوجه: هنگامی که بازسپاری ارسالی توسط دریافت کننده خوانده شده باشد، با ایکن نمایش داده شده در تصویر زیر نمایش داده می شود. شکل 45\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\r&nbsp;شکل 46 آیکن ارسال بازسپاری و خوانده شده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\rتوجه: هنگامی که بازسپاری دریافت می شود، با ایکن نمایش داده شده در تصویر زیر نمایش داده می شود. شکل 47\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rشکل 47 آیکن دریافت بازسپاری\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r\n\r\n\rتوجه: هنگامی که بازسپاری دریافتی توسط دریافت کننده خوانده نشده باشد ، با ایکن نمایش داده شده در تصویر زیر و نیز علامت هشدار نمایش داده می شود. شکل 48\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rشکل 48 آیکن دریافت بازسپاری و خوانده نشده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rتوجه: هنگامی که بازسپاری دریافتی توسط دریافت کننده خوانده شده باشد ، با ایکن نمایش داده شده در تصویر زیر نمایش داده می شود. شکل 49\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rشکل 49 آیکن دریافت بازسپاری و خوانده شده توسط دریافت کننده\n\r\n\r&nbsp;\n\r\n\r8. تعریف باز سپاری جدید \n\r\n\r\n\r\n\rامکان تعریف بازسپاری جدید برای یک درخواست با استفاده از دکمه ثبت بازسپاری فراهم شده است. با انتخاب یک درخواست و کلیک بر این دکمه، فرم ثبت بازسپاری نمایش داده می شود که با تکمیل اطلاعات کلی بازسپاری، شرح بازسپاری، دریافت کنندگان، پیوست و یادآوری ، یک بازسپاری جدید به فهرست بازسپاری های موجود درخواست افزوده می شود. شکل50 \n\r\n\r&nbsp;\n\r\n\r\n\r\n\rشکل50 دکمه ثبت بازسپاری \n\r\n\rتوجه: مقادیر فیلد گروه بازسپاری می تواند \"وقفه گسترده\"، \"وقفه در کار بخشی از سامانه\"، \"اعلام خطا\"، \"درخواست تغییر\" ، \"درخواست جدید\"، \"درخواست راهنمایی\" و \"اطلاع رسانی\" باشد. \n\r\n\r&nbsp;\n\r\n\r\n\r\n\rتوجه: با راست کلیک کردن روی هر درخواست نیز می توان به گزینه ثبت بازسپاری دست یافت. شکل 51\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل&nbsp;51&nbsp; دسترسی به گزینه ثبت بازسپاری با راست کلیک\n\r\n\r&nbsp;\n\r\n\rتوجه: با دابل کلیک کردن روی هر درخواست نیز باید بتوان به فرم ثبت بازسپاری دست یافت.\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r9. ویرایش بازسپاری \n\r\n\r&nbsp;\n\r\n\rبرای ویرایش اطلاعات بازسپاری نظیر اطلاعات کلی بازسپاری، شرح بازسپاری، دریافت کنندگان، پیوست و یادآوری می توان با راست کلیک کردن روی رکورد مربوطه به فرم ویرایش بازسپاری دست یافت و تغییرات مورد نظر را اعمال کرد.\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rتوجه: امکان ویرایش بازسپاری فقط زمانی وجود دارد که بازسپاری توسط دریافت کننده آن مشاهده نشده باشد. شکل 52\n\r\n\r\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل&nbsp;52 گزینه ویرایش بازسپاری\n\r\n\r&nbsp;\n\r\n\r10. حذف بازسپاری \n\r\n\r&nbsp;\n\r\n\rبرای حذف اطلاعات بازسپاری می توان با راست کلیک کردن روی رکورد مربوطه به گزینه حذف بازسپاری دست یافت.\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rتوجه: امکان حذف بازسپاری فقط زمانی وجود دارد که توسط دریافت کننده آن مشاهده نشده باشد. شکل 53\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp; شکل 53 گزینه حذف بازسپاری\n\r\n\r&nbsp;\n\r\n\r11.&nbsp; ثبت درخواست مرتبط\n\r\n\r\n\r\n\r\n\r\n\rامکان ثبت درخواست مرتبط با یک درخواست با استفاده از دکمه ثبت درخواست مرتبط فراهم شده است. با کلیک بر این دکمه، همان فرم ثبت درخواست نمایش داده می شود که در فیلد ثبت درخواست مرتبط آن، شماره درخواست اول نمایش داده می شود. شکل 54 شکل 55\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rتوجه: ثبت درخواست مرتبط برای درخواست هایی انجام می شود که بسته شده اند ولی لازم است اقدامات جدیدی انجام شود. در صورتی که درخواستی بسته نشده باشد جهت تکمیل اطلاعات می توان از ثبت بازسپاری استفاده نمود و یا در حالتی که هنوز توسط دریافت کننده مشاهده نشده باشد از ویرایش استفاده کرد. \n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 54 دکمه ثبت درخواست مرتبط\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 55 نمایش شماره درخواست مرتبط\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r&nbsp;","پوشه کار","topics\\پوشه_کار.htm","\n\r\n\rپیش‏گفتار                               \n\r\n\rیکی از عواملی که در موفقیت کاری شرکت تاثیر بسزایی دارد، ارتباط تضمین شده و بدون تاخیر مشتریان با شرکت و همچنین ارتباط کلیه بخش های شرکت با یکدیگر می باشد                              .\n\r\n\rسیستم مکانیزه مدیریت درخواست خدمت    ، برنامه ای است که با در نظر گرفتن این عامل پا به عرصه گذاشته و در مدت زمان کوتاهی به یکی از پر                        طرفدار ترین  سیستمهای                           میز خدمت بدل شده است    .\n\r\n\rاز عمده دلایل موفقیت این نوع خدمت رسانی متمرکز را می‌توان به امکان ارسال درخواست کاربران و تبدیل آن به برچسب، رابط کاربری عامه پسند این برنامه که به طریقی طراحی شده که کاربر در کمترین زمان به جواب خود رسیده و یا درخواست خود را به بخش مربوطه ارسال می کند.                                                   \n\r\n\rبه طور کلی سیستم مدیریت درخواست خدمت، تمامی مشکلات و نارسایی های ارتباط مشتری با واحد پشتیبانی را مرتفع کرده و با ارائه شاخصه هایی کارآمد، کمک شایانی به مدیریت خدمت مشتریان نموده است                                 .\n\r\n\r\n\rشرکت اطلاع‏رسانی پیوند داده‏ها به ‏طور رسمی اعلام می‌نماید که مستند مربوطه را مطابق با مشخصات و ویژگی‏های برنامه مدیریت درخواست خدمت و بعنوان راهنمای جامع این برنامه تهیه نموده و در عین حال از دقت لازم و کافی در تمام مراحل اجرایی کار برخوردار خواهد بود. ","پیش‏گفتار","topics\\پیش‏گفتار.htm","\n\r\n\rتعریف درخواست جدید\n\r\n\rامکان تعریف درخواست جدید با استفاده از دکمه ثبت درخواست فراهم شده است. با کلیک بر این دکمه، فرم ثبت درخواست نمایش داده می شود که با تکمیل اطلاعات کلی درخواست، شرح درخواست، دریافت کنندگان، پیوست و یادآوری ، یک درخواست جدید به فهرست درخواست های موجود افزوده می شود.&nbsp; شکل 56\n\r\n\r\n\r\n\r&nbsp;شکل&nbsp;56&nbsp; افزودن درخواست   \n\r\n\rتوجه: امکان ثبت درخواست فقط توسط مرکز وجود دارد و پیوند اجازه ثبت درخواست را ندارد.&nbsp;\n\r\n\r&nbsp;\n\r\n\rتوجه: مقادیر فیلد اولویت می تواند \"کم\"، \"روتین\"، \"بالا\"، \"خیلی بالا\" و \"اورژانسی\" باشد و به طور پیشفرض گزینه \"روتین\" نمایش داده می شود.\n\r\n\r                      &nbsp;\n\r\n\rتوجه: مقادیر فیلد گروه درخواست می تواند \"وقفه گسترده\"، \"وقفه در کار بخشی از سامانه\"، \"اعلام خطا\"، \"درخواست تغییر\" ، \"درخواست جدید\"، \"درخواست راهنمایی\" و \"اطلاع رسانی \" باشد.\n\r\n\r  &nbsp;\n\r\n\r   \n\r\n\rتوجه: امکان کپی&nbsp; و انتقال اطلاعات در قسمت شرح درخواست وجود دارد و کاربر می تواند با توجه به نیاز خود فونت، رنگ، اندازه، پس زمینه و استایل متن خود را انتخاب نماید.\n\r\n\r                             &nbsp;\n\r\n\r                             \n\r\n\rتوجه: هر مدیر مرکز فقط درخواست هایی که به او ارجاع داده شده است را در پوشه کار خود می بیند و سایر درخواست های مرکز را نمی بیند\n\r\n\r&nbsp;\n\r\n\r1. وضعیت درخواست   \n\r\n\r&nbsp;\n\r\n\r\n\r\n\rامکان تعیین وضعیت درخواست در قسمت دریافت کنندگان وجود دارد.( شکل 56) فیلد \"وضعیت\" در قسمت دریافت کنندگان فقط برای مدیر مرکز قابل مشاهده می باشد و برای کاربران مرکز قابل مشاهده نمی باشد. مقادیر فیلد \"وضعیت\" در قسمت دریافت کنندگان، \"داخلی\" و \"ارسال به پیوند\" می باشد.\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 56 وضعیت درخواست\n\r\n\r&nbsp;\n\r\n\rتوجه: مقدار پیشفرض فیلد وضعیت، گزینه \"ارسال به پیوند\" می باشد.\n\r\n\r         &nbsp;\n\r\n\r         \n\r\n\rتوجه: در صورتی که مدیر مرکز بخواهد درخواستی را برای پیوند ارسال کند می بایست گزینه \"ارسال به پیوند\" را انتخاب کند و در اینصورت وضعیت درخواست \"ارسال به پیوند\" می شود.\n\r\n\r  &nbsp;\n\r\n\r  \n\r\n\rتوجه: در صورتی که مدیر مرکز &nbsp;بخواهد درخواستی را برای کاربران و مدیران مرکز ارسال کند می بایست گزینه \"داخلی\" را انتخاب نماید و در اینصورت وضعیت درخواست \" داخلی \" می شود.\n\r\n\r           &nbsp;\n\r\n\r           \n\r\n\rتوجه: کاربران مرکز&nbsp; تنها می توانند درخواست را برای مسئولین مرکز خود ارسال نمایند، در این حالت وضعیت درخواست بطور اتوماتیک \"داخلی\" می شود.\n\r\n\r&nbsp;\n\r\n\r2. انتخاب دریافت کنندگان   \n\r\n\r   &nbsp;\n\r\n\rبا استفاده از دکمه انتخاب دریافت کنندگان می توان دریافت کنندگان درخواست را مشخص کرد. با کلیک بر این دکمه، فرم انتخاب دریافت کنندگان نمایش داده می شود که از طریق آن می توان دریافت کنندگان درخواست را مشخص کرد.&nbsp; ‏شكل 57‏ شكل 58   \n\r\n\r   &nbsp;\n\r\n\r   \n\r\n\rشکل 57  دکمه انتخاب دریافت کنندگان   \n\r\n\r   &nbsp;\n\r\n\r   \n\r\n\r   &nbsp;\n\r\n\rشکل 58  انتخاب دریافت کنندگان   \n\r\n\r   &nbsp;\n\r\n\rتوجه: فیلد \"یادداشت\" در فرم انتخاب دریافت کنندگان، امکان ثبت یادداشتی خصوصی برای دریافت کننده ای خاص را فراهم می کند.\n\r\n\r                   &nbsp;\n\r\n\r                   \n\r\n\rتوجه: &nbsp;هنگامی که گزینه \"ارسال به پیوند\" از قسمت \"نوع ارسال\" انتخاب می شود، قسمت \"دریافت کنندگان اصلی\" از فرم انتخاب دریافت کنندگان غیر فعال می شود و &nbsp;انتخاب کارشناس میز خدمت براساس تقسیم وظایف درج شده در سیستم بر عهده سامانه است.\n\r\n\rتوجه: هنگامی که درخواستی برای پیوند ارسال می شود، امکان انتخاب کارشناسان و مسئولین داخلی مرکز به عنوان \"سایر دریافت کنندگان\" یا \"دریافت کنندگان محرمانه\" وجود دارد.\n\r\n\r    &nbsp;\n\r\n\r    \n\r\n\rتوجه: کاربر مرکز در قسمت دریافت کنندگان فقط مدیران مرکز را می بیند\n\r\n\r&nbsp;\n\r\n\r3. افزودن پیوست\n\r\n\r&nbsp;\n\r\n\rبا استفاده از دکمه افزودن پیوست می توان، فایل های مرتبط با درخواست را پیوست کرد. با کلیک بر این دکمه، فرم انتخاب فایل جهت انتخاب فایل های پیوست نمایش داده می شود. شکل 59\n\r\n\r&nbsp;\n\r\n\r   \n\r\n\r&nbsp;\n\r\n\rشکل 59  دکمه افزودن پیوست\n\r\n\r&nbsp;\n\r\n\r4. افزودن یادآوری\n\r\n\r&nbsp;\n\r\n\r\n\r\n\rدر قسمت یادآوری می توان تاریخ یادآوری، ساعت یادآوری، روش یادآوری و همچنین یادداشتی جهت یادآوری را مشخص کرد. یادداشت یادآوری در تاریخ و ساعت مشخص شده و با روش تعیین شده برای ثبت کننده درخواست ارسال می شود. روش های یادآوری ایمیل و پیامک می باشد. شکل 60\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r   \n\r\n\r   &nbsp;\n\r\n\rشکل 60&nbsp;  افزودن یادآوری&nbsp;\n\r\n\r   &nbsp;\n\r\n\rتوجه: مقدار پیشفرض ساعت یادآوری 8 صبح می باشد.\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r               \n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r        &nbsp;   ","درخواست","topics\\درخواست.htm","\n\r\n\rتعریف کاربران برنامه\n\r\n\rدر قسمت تعریف کاربران برنامه امکان نمایش فهرست کاربران، جستجوی کاربران براساس نام، نام خانوادگی و نام پدر وجود دارد. شکل 12\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; شكل 12 اطلاعات کاربران \n\r\n\r1.&nbsp; نمایش فهرست کاربران    \n\r\n\rاطلاعات کاربران نظیر نام و نام خانوادگی، نام پدر، جنسیت، تاریخ تولد، محل تولد، کد ملی و ایمیل در این قسمت قابل مشاهده می باشد. شکل 13 \n\r\n\r &nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; شكل 13 نمایش فهرست کاربران\n\r\n\r \n\r\n\r2.&nbsp; جستجوی کاربران \n\r\n\r &nbsp;\n\r\n\rجهت جستجوی اطلاعات کاربران می‏توان از فیلدهای نام، نام خانوادگی و نام پدر در قسمت بالای صفحه استفاده نمود و در اسرع وقت به اطلاعات مورد نیاز دست یافت. شکل 14\n\r\n\r \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; شكل 14 جستجوی کاربران \n\r\n\r &nbsp;\n\r\n\r3.&nbsp; تعریف کاربر جدید \n\r\n\r &nbsp;\n\r\n\rامکان تعریف کاربر جدید با استفاده از دکمه افزودن کاربر فراهم شده است. با کلیک بر این دکمه، فرم افزودن کاربر نمایش داده می شود که با تکمیل اطلاعات هویتی کاربر ، یک کاربر جدید به فهرست کاربران موجود افزوده می شود. شکل 15 شکل 16\n\r\n\r   &nbsp;\n\r\n\r\n\r\n\rتوجه: فقط نقش های مدیر مرکز و مدیر پیوند به این فرم دسترسی دارند. \n\r\n\r &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل5 1 دکمه افزودن کاربر\n\r\n\r\n\r\n\r&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;  \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; شكل&nbsp;&nbsp;16 افزودن کاربر\n\r\n\r&nbsp;\n\r\n\r4.&nbsp;ویرایش اطلاعات کاربر\n\r\n\r&nbsp;\n\r\n\rبرای ویرایش اطلاعات کاربر نظیر نام و نام خانوادگی، نام پدر، جنسیت، تاریخ تولد، محل تولد، کد ملی و ایمیل می توان با کلیک بر دکمه ویرایش به فرم ویرایش اطلاعات کاربر دست یافت و تغییرات مورد نظر را اعمال کرد. شکل 17 شکل 18\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; شكل17دکمه ویرایش\n\r\n\r&nbsp;\n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\r\n\r&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; شكل 18 ویرایش اطلاعات کاربر\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r","کاربران","topics\\کاربران.htm","\n\r\n\rگزارشات                                            \n\r\n\r                                            &nbsp;\n\r\n\rدر قسمت گزارشات، امکان گزارش گیری براساس فیلدهای عنوان، استان، گروه مرکز، نام مرکز، وضعیت، اولویت، تاریخ ثبت، گروه درخواست، سیستم و زیرسیستم فراهم شده است. با مشخص کردن فیلترهای گزارش و کلیک بر دکمه نمایش گزارش، گزارش مربوطه نمایش داده می شود. شکل 64\n\r\n\r                                            \n\r\n\rتوجه: با توجه به نقش کاربر، تعداد فیلدهای نمایش داده شده متغیر می باشد.\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\r&nbsp;\n\r\n\rشکل 64 &nbsp; دکمه نمایش گزارش\n\r\n\r&nbsp;","گزارشات","topics\\گزارشات.htm","\n\r\n\r&nbsp;1-&nbsp; نمایش فهرست کاربران\n\r2-&nbsp; جستجوی کاربران براساس اطلاعات هویتی\n\r3-&nbsp; تعریف کاربر جدید\n\r4-&nbsp; ویرایش اطلاعات کاربر\n\r5-&nbsp; نمایش فهرست درخواست ها\n\r6-&nbsp; جستجوی درخواست ها بر اساس اطلاعات مختلف\n\r7-&nbsp; تعریف درخواست جدید\n\r8-&nbsp; ویرایش اطلاعات درخواست\n\r9-&nbsp; نمایش فهرست بازسپاری ها\n\r10-&nbsp; تعریف بازسپاری جدید\n\r11-&nbsp; ویرایش اطلاعات بازسپاری\n\r12-&nbsp; ثبت درخواست مرتبط","","topics\\مروری_بر_قابلیت‏های_گوناگون_برنامه_مدیریت_درخواست_خدمت.htm","کلیات برنامه مدیریت درخواست خدمتمقدمه ساماندهی فرآیندهای مرتبط با تعریف و مدیریت درخواست مراکز، در قالب برنامه مدیریت درخواست خدمت انجام می‏شود. این برنامه بصورت‏‏ تحت وب و دارای قابلیت متمرکز و از راه دور توسط کارشناسان شرکت اطلاع رسانی پیوند داده‏ها توسعه یافته است. در نگارش حاضر برنامه، امکان ثبت، ویرایش و مشاهده درخواست ها وجود دارد.     ","مقدمه","topics\\مقدمه.htm","\n\r\n\rشرح چگونگی کار با برنامه مدیریت درخواست خدمت\n\r\n\rمقدمه\n\rایجاد بستری مناسب برای مدیریت بهتر درخواست های مشتریان با ارائه برنامه مدیریت درخواست خدمت شرکت اطلاع‏رسانی پیوند داده‏ها محقق گردیده است. این برنامه، فرآیندهای مرتبط با تعریف و مدیریت درخواست های مشتریان را ساماندهی می کند \n\r\n\r&nbsp;","مقدمه","topics\\مقدمه1.htm","\n\r\n\rورود اطلاعات کاربران \n\r\n\rدر طرح توسعه جامع برنامه‏ها، امکان ورود یکباره و دسترسی به صفحه‏ پورتال که در آن فهرست تمامی برنامه‏های پیوند وجود دارد، فراهم شده است. از طریق مرورگر و با درج آدرس برنامه در&nbsp;URL ، به صفحه ورود اطلاعات کاربری، وارد خواهید شد. با درج نام کاربری، رمز عبور و رقم کد امنیتی، صفحه پورتال جامع برنامه‏های پیوند در دسترس قرار می‏گیرد. امکان به خاطر سپاری رمز عبور نیز در پنل ورود اطلاعات کاربری وجود دارد. ‏شكل 1\n\r\n\r\n\r\n\rشکل 1  ورود اطلاعات کاربری\n\r\n\rدر صورتی که رمز عبور خود را فراموش کرده اید، روی گزینه \"رمز عبور خود را فراموش کرده ام\" کلیک کنید. در این صورت صفحه فراموشی رمز عبور برای درخواست بازیابی رمز جدید نمایش داده می شود. شکل 2\n\r\n\r\n\r\n\rشکل 2  بازیابی رمز جدید\n\r\n\rبا ورود نام کاربری و عبارت امنیتی معتبر و سپس کلیک بر دکمه بازیابی، ایمیلی حاوی لینک بازیابی رمز عبور برایتان ارسال می شود. با کلیک بر لینک ایمیل شده، کلمه عبور تصادفی جدیدی از طریق ایمیل برایتان ارسال می شود. با ورود کلمه عبور جدید خودکار می توانید وارد برنامه شوید.\n\r\n\r&nbsp;\n\r\n\rورود به صفحه پورتال جامع برنامه‏های پیوند\n\r\n\rپس از ورود اطلاعات کاربری، دسترسی به صفحه پورتال جامع برنامه‏های پیوند ممکن می‏گردد. فهرست برنامه‏ها در منوی موجود در بالای صفحه وجود دارد. از طریق لینک‏های تصویری موجود در صفحه نیز، ورود به هریک از برنامه‏ها امکان‏پذیر است. اطلاعات کلی در خصوص شرکت پیوند داده‏ها و نیز را‏ه‏های تماس با کارشناسان پیوند نیز از طریق لینک‏های مربوطه قابل دسترسی است. ‏شكل 3                                   \n\r\n\r                                   \n\r\n\rشکل 3  پورتال جامع برنامه های پیوند                                   \n\r\n\rورود به برنامه مدیریت درخواست خدمت                                   \n\r\n\rبرای ورود به برنامه مدیریت درخواست خدمت از لینک تصویری می‏توان استفاده کرد. شکل 4\n\r\n\r&nbsp;\n\r\n\r\n\r\n\r&nbsp;\n\r\n\rشکل 4 &nbsp;&nbsp; ورود به برنامه مدیریت درخواست خدمت از طریق لینک تصویری         ","ورود","topics\\ورود.htm");